import {
  Card, CardImg, CardImgOverlay, CardBody, CardSubtitle, CardText,
  CardTitle, Breadcrumb, BreadcrumbItem
} from 'reactstrap';
import { Link } from 'react-router-dom';




function RenderMenuItem({ item, onClick }) {
  return (
    <div class='body-item'>
      <div className='row' style={{ alignItems: 'center' }}>

        {/* <img class="home-item__img" alt={item.name} src={item.image} /> */}
        {/* <div style={{ padding: '0 20px' }}>
  <h4 class="home-item__name">{item.title} </h4>
  <h6 class="home-item__description">
    {item.description}
  </h6>
  <Link to={`/home/${item.id}`} >
    <a href="#" class="btn  btn-lg active buy-button bg-light btn-outline-light text-center m-2 " role="button" aria-pressed="true">
      <img class="home-item__img-button" alt="" src="/assets/images/481127.png" />
    </a>
  </Link>
</div> */}
        <div className='col-12 col-sm-2 '>
          <div class='body-item-date'>
            <h6 class='body-item-date-month'>December
            </h6>
            <h6 class='body-item-date-year'>2023
            </h6>
            <h3 class='body-item-date-day'>08
            </h3>
          </div>
        </div>
        <div className='col-12 col-sm-8'>
          <div class="body-item-name-course">React JS-TranLQ-StudentID:12345</div>
        </div>
        <div className='col-13 col-sm-2'>
          <h5 class="body-item-amount text-center">2023</h5>
        </div>








      </div>
    </div>


  );
}


const Home = (props) => {

  const home = props.items.map((item) => {
    return (
      <div style={{ marginBottom: '10px' }} key={item.id}>
        <RenderMenuItem item={item}
        />
      </div>
    );
  });

  return (
    <div class="body" style={{ backgroundColor: '#3f3f3f' }}>
      <div class='body-item'>
        <div className='row' style={{ alignItems: 'center', marginBottom: '10px' }}>
          <div className='col-12 col-sm-2 '>
            <div class='body-item-date'>
              <h6 class='body-item-date-month'>December
              </h6>
              <h6 class='body-item-date-year'>2023
              </h6>
              <h3 class='body-item-date-day'>08
              </h3>
            </div>
          </div>
          <div className='col-12 col-sm-8'>
            <div class="body-item-name-course">React JS-TranLQ-StudentID:12345</div>
          </div>
          <div className='col-13 col-sm-2'>
            <h5 class="body-item-amount text-center">2023</h5>
          </div>


        </div>
        {home}
      </div>






    </div>

  );


}

export default Home;
